import React, { useEffect, useState } from 'react'
import useGlobalState from '../utils/useGlobalState'

const pkg = require('../../package.json')

function GridCreator() {
    const globalState = useGlobalState()
    const [dimensions, setDimensions] = useState([10, 10])
    const [dimensionsFrozen, setDimensionsFrozen] = useState(false)
    const [solutions, setSolutions] = useState([])
    const [selectedBlock, setSelectedBlock] = useState(null)
    const [title, setTitle] = useState('')
    const [def, setDef] = useState([[], []])
    const { username, api_token } = globalState.user

    const onChangeWidth = (ev) => {
        setDimensions([ev.target.value, dimensions[1]])
    }
    const onChangeHeight = (ev) => {
        setDimensions([dimensions[0], ev.target.value])
    }

    const freezeDimensions = () => {
        setDimensionsFrozen(true)

        const arr = []
        const arr2 = [[], []]
        for (let i=0; i < dimensions[1]; i++) {
            arr.push([])
            arr2[0].push('')
            for (let j=0; j < dimensions[0]; j++) {
                arr[i].push('')
            }
        }
        for (let j=0; j < dimensions[0]; j++) {
            arr2[1].push('')
        }
        setSolutions(arr)
        setDef(arr2)
    }

    const selectBlock = (i, j) => {
        setSelectedBlock([i, j])
    }

    const setSolutionXY = (i, j, e) => {
        e.preventDefault()
        const char = e.key.toLowerCase()
        if (char.length === 1 && /[a-z -]/.test(char)){
            const newSol = solutions;
            newSol[i][j] = char.toUpperCase()
            setSolutions(newSol)
            setSelectedBlock(null);
            document.activeElement.blur();
        }
        if(char === 'backspace'){
            const newSol = solutions;
            newSol[i][j] = ''
            setSolutions(newSol)
            setSelectedBlock(null);
            document.activeElement.blur();
        }
    }

    const setHorizontalDef = (i, txt) => {
        const tmp = def
        tmp[0][i] = txt
        setDef(tmp)
    }

    const setVerticalDef = (i, txt) => {
        const tmp = def
        tmp[1][i] = txt
        setDef(tmp)
    }

    const romanize = (num) => {
        if (isNaN(num))
            return NaN;
        var digits = String(+num).split(""),
            key = ["","C","CC","CCC","CD","D","DC","DCC","DCCC","CM",
                   "","X","XX","XXX","XL","L","LX","LXX","LXXX","XC",
                   "","I","II","III","IV","V","VI","VII","VIII","IX"],
            roman = "",
            i = 3;
        while (i--)
            roman = (key[+digits.pop() + (i * 10)] || "") + roman;
        return Array(+digits.join("") + 1).join("M") + roman;
    }

    const save = async () => {
        const response = await fetch(process.env.REACT_APP_API_URL+'/v1/grid/new', {
            method: 'POST',
            credentials: 'omit',
            headers: {
                'Authorization': 'Token ' + api_token,
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                width: dimensions[0],
                height: dimensions[1],
                title: title !== '' ? title : 'Grille sans titre',
                solution: solutions.map(l=>l.map(c=> (c==='' ? '_' : c)).join('')).join(''),
                definitions: def,
            })
        })

    }
    const publish = async () => {
        await fetch(process.env.REACT_APP_API_URL+'/v1/grid/new', {
            method: 'PATCH',
            credentials: 'omit',
            headers: {
                'Authorization': 'Token ' + api_token,
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({
                width: dimensions[0],
                height: dimensions[1],
                title: title !== '' ? title : 'Grille sans titre',
                solution: solutions.map(l=>l.map(c=> (c==='' ? '_' : c)).join('')).join(''),
                definitions: def
            })
        })
    }
    const isGridFull = () => {
        return solutions.map(l=>l.map(c=> c).join('')).join('').length === dimensions[0]*dimensions[1]
    }
    return (
        <div className="container main-container">
            <h1>Nouvelle grille</h1>

            {!dimensionsFrozen && (<>
                <h3>Dimensions</h3>
                <label for="width">Largeur</label>
                <input name="width" type="number" min="1" onChange={onChangeWidth} defaultValue={dimensions[0]}></input>
                <label for="height">Hauteur</label>
                <input name="height" type="number" min="1" onChange={onChangeHeight} defaultValue={dimensions[1]}></input>
                <button class="btn btn-primary" onClick={freezeDimensions}>Continuer</button>
            </>)}
            {dimensionsFrozen && (<><input type='text' onChange={(e)=>setTitle(e.target.Value)} placeholder="Titre de la grille"></input><table>
                <tr><td> </td>{solutions[0].map((val, j)=>(<td style={{textAlign: 'center'}}>{romanize(j+1)}.</td>))}</tr>
                { solutions.map((line, i)=>(
                    <tr><td>{i+1}.</td>{line.map((val, j)=>(<td style={{width: '2em', height: '2em', border: '1px solid #000'}}><input type='text' onFocus={() => selectBlock(i, j)} onBlur={() => selectBlock(null)} style={{outline: 'none', textAlign: 'center', border: '0', caretColor: 'transparent', width: '2em', backgroundColor: ((selectedBlock && (selectedBlock[0] === i && selectedBlock[1] === j)) ? 'red' : (val === ' ' ? 'black' : 'white'))}} onKeyDown={(e) => setSolutionXY(i, j, e)} readOnly value={solutions[i][j] ? val : ''}/></td>))}</tr>
                ))
                }
                </table>
                <p>Cliquer sur une case et taper la lettre désiré, Espace pour noircire la case, Retour arrière pour re-initialiser la case.</p>
                <div style={{marginTop: '15px'}}>
                    <button class="btn btn-primary save-btn" onClick={save}>Sauvegarder</button>
                </div>
                <h3>Définitions</h3>
                <h4>Horizontalement</h4>
                <div>
                {solutions.map((l, i) => (
                    <div style={{margin: '5px'}}><span style={{width: '3em', display: 'inline-block'}}>{i+1}. </span><input  style={{width: '500px'}} type="text"  onChange={(e) => setHorizontalDef(i, e.target.value)}></input></div>
                ))}
                </div>
                <h4>Verticalement</h4>
                <div>
                {solutions[0].map((l, i) => (
                    <div style={{margin: '5px'}}><span style={{width: '3em', display: 'inline-block'}}>{romanize(i+1)}. </span><input style={{width: '500px'}} type="text" onChange={(e) => setVerticalDef(i, e.target.value)}></input></div>
                ))}
                </div>
                <div style={{marginTop: '15px'}}>
                    <button class="btn btn-primary save-btn" onClick={save}>Sauvegarder</button>
                </div>
                {isGridFull() && <div style={{marginTop: '15px'}}>
                    <button class="btn btn-danger">Publier</button>
                </div>}
                </>)
            }
        </div>
    )
}

export default GridCreator
