import React, { useEffect, useState } from 'react'
import useGlobalState from '../utils/useGlobalState'
import { useParams } from "react-router-dom";
import { useReactToPrint } from 'react-to-print';
const pkg = require('../../package.json')


function OwnGrids() {
    const globalState = useGlobalState()
    const [list, setList] = useState('')
    const { username, api_token } = globalState.user

    const loadGrids = async () => {
        try {
            const r = await fetch(process.env.REACT_APP_API_URL+'/user/'+username,{
                method: 'GET',
                credentials: 'omit',
                headers: {
                    'Authorization': 'Token ' + api_token,
                    'Content-Type': 'application/json'
                }
            })
            const list = await r.json()
            setList(list.crosswords_grids)
        } catch(e) {
            window.location = '/'
        }
    }

    useEffect(()=>{
        (async () => (await loadGrids()))()     
    }, [])

    return (
        <div className="container main-container">
            { list.length && (list.map((e=>(
                <div>
                    <h3>{e.title}</h3>
                    <span>Créée {e.creation_date}</span><br/>
                    <span>Modifiée {e.modification_date}</span>
                </div>
            ))))
            }
        </div>
    )
}

export default OwnGrids
