import React, { useEffect, useState } from 'react'
import useGlobalState from '../utils/useGlobalState'
import { useParams } from "react-router-dom";
const pkg = require('../../package.json')

function GridEditor(props) {
    const globalState = useGlobalState()
    const [dimensions, setDimensions] = useState([10, 10])
    const [dimensionsFrozen, setDimensionsFrozen] = useState(false)
    const [solutions, setSolutions] = useState([])
    const [selectedBlock, setSelectedBlock] = useState(null)
    const [title, setTitle] = useState('')
    const [def, setDef] = useState([[], []])
    const { username, api_token } = globalState.user
    const {uid: gridId} = useParams();


    const loadGrid = async (id) => {
        try {
            const r = await fetch(process.env.REACT_APP_API_URL+'/grid/'+id)
            const d = await r.json()
            setDimensions([d.width, d.height])
            setTitle(d.title)
            const a = []
            for (let j=0; j< d.height; j++){
                a.push([])
                for (let i=0; i< d.width; i++){
                    const ch = d.grid[j*d.width+i]
                    a[j].push(ch === '_' ? '': ch.toUpperCase())
                }
            }
            setSolutions(a)
            setDef(d.definitions)
            setDimensionsFrozen(true)
        } catch(e) {
            window.location = '/404'
        }
    }

    useEffect(()=>{
        if (gridId) {
            (async () => (await loadGrid(gridId)))()
        }

    }, [gridId])

    const onChangeWidth = (ev) => {
        setDimensions([ev.target.value, dimensions[1]])
    }

    const onChangeHeight = (ev) => {
        setDimensions([dimensions[0], ev.target.value])
    }

    const freezeDimensions = () => {
        setDimensionsFrozen(true)

        const arr = []
        const arr2 = [[], []]
        for (let i=0; i < dimensions[1]; i++) {
            arr.push([])
            arr2[0].push('')
            for (let j=0; j < dimensions[0]; j++) {
                arr[i].push('')
            }
        }
        for (let j=0; j < dimensions[0]; j++) {
            arr2[1].push('')
        }
        setSolutions(arr)
        setDef(arr2)
    }

    const selectBlock = (i, j) => {
        setSelectedBlock([i, j])
    }

    const setSolutionXY = (i, j, e) => {
        e.preventDefault()
        if(solutions[i][j] === ' ') return
        const char = e.key.toLowerCase()
        if (char.length === 1 && /[a-z -]/.test(char)){
            const newSol = solutions;
            newSol[i][j] = char.toUpperCase()
            setSolutions(newSol)
            setSelectedBlock(null);
            document.activeElement.blur();
        }
        if(char === 'backspace'){
            const newSol = solutions;
            newSol[i][j] = ''
            setSolutions(newSol)
            setSelectedBlock(null);
            document.activeElement.blur();
        }
    }

    const romanize = (num) => {
        if (isNaN(num))
            return NaN;
        var digits = String(+num).split(""),
            key = ["","C","CC","CCC","CD","D","DC","DCC","DCCC","CM",
                   "","X","XX","XXX","XL","L","LX","LXX","LXXX","XC",
                   "","I","II","III","IV","V","VI","VII","VIII","IX"],
            roman = "",
            i = 3;
        while (i--)
            roman = (key[+digits.pop() + (i * 10)] || "") + roman;
        return Array(+digits.join("") + 1).join("M") + roman;
    }

    const isGridFull = () => {
        return solutions.map(l=>l.map(c=> c).join('')).join('').length === dimensions[0]*dimensions[1]
    }
    return (
        <div className="container main-container">
            <h1>{title}</h1>
            { solutions.length && (<><table>
                <tr><td> </td>{solutions[0].map((val, j)=>(<td style={{textAlign: 'center'}}>{romanize(j+1)}.</td>))}</tr>
                { solutions.map((line, i)=>(
                    <tr><td>{i+1}.</td>{line.map((val, j)=>(<td style={{width: '2em', height: '2em', border: '1px solid #000'}}><input type='text' onFocus={() => selectBlock(i, j)} onBlur={() => selectBlock(null)} style={{outline: 'none', textAlign: 'center', border: '0', caretColor: 'transparent', width: '2em', backgroundColor: ((selectedBlock && (selectedBlock[0] === i && selectedBlock[1] === j)) ? 'red' : (val === ' ' ? 'black' : 'white'))}} onKeyDown={(e) => setSolutionXY(i, j, e)} readOnly value={solutions[i][j] ? val : ''}/></td>))}</tr>
                ))
                }
                </table>
                <p>Cliquer sur une case et taper la lettre désiré, Retour arrière pour re-initialiser la case.</p>
                <h3>Définitions</h3>
                <h4>Horizontalement</h4>
                <div>
                {def[0].map((l, i) => (
                    <div style={{margin: '5px'}}><span style={{width: '3em', display: 'inline-block'}}>{i+1}. </span><span>{l}</span></div>
                ))}
                </div>
                <h4>Verticalement</h4>
                <div>
                {def[1].map((l, i) => (
                    <div style={{margin: '5px'}}><span style={{width: '3em', display: 'inline-block'}}>{romanize(i+1)}. </span><span>{l}</span></div>
                ))}
                </div>
                </>)
            }
        </div>
    )
}

export default GridEditor
